import * as angular from 'angular';

export type IStorageProvider = angular.storage.IStorageProvider;
export type ILocalStorageProvider = angular.storage.ILocalStorageProvider;
export type ISessionStorageProvider = angular.storage.ISessionStorageProvider;
export type IStorageService = angular.storage.IStorageService;

declare module 'angular' {
    export namespace storage {
        interface IStorageProvider extends angular.IServiceProvider {
            setKeyPrefix(prefix: string): void;
            setSerializer(s: Function): void;
            setDeserializer(s: Function): void;
            get(key: string): any;
            set(key: string, value: any): void;
        }

        interface ILocalStorageProvider extends IStorageProvider {}
        interface ISessionStorageProvider extends IStorageProvider {}

        interface IStorageService {
            [key: string]: any;
        }
    }
}